		/// <summary>
		/// Starts a IDbTransaction against the Database
		/// </summary>
		/// <returns>An IDbTransaction</returns>
		public IDbTransaction BeginTransaction()
		{
			StateTransaction(false);
			_transaction = _connection.BeginTransaction();
			return _transaction;
		}

		/// <summary>
		/// Commits an IDbTransaction
		/// </summary>
		public void CommitTransaction()
		{
			StateTransaction(true);
			_transaction.Commit();
			_transaction = null;
		}

		/// <summary>
		/// Rollbacks an IDbTransaction
		/// </summary>
		public void RollbackTransaction()
		{
			StateTransaction(true);
			_transaction.Rollback();
			_transaction = null;
		}

		private void StateTransaction(bool mustBeOpen)
		{
			if(mustBeOpen)
			{
				if(_transaction == null)
					throw new InvalidOperationException("La transazione non è aperta.");
			}
			else
			{
				if(_transaction !=  null)
					throw new InvalidOperationException("La transazione è già aperta.");
			}
		}

		/// <summary>
		/// Creates an IDbCommand
		/// </summary>
		/// <param name="sqlText">The SQL statement for the IDbCommand</param>
		/// <param name="procedure">true to use a procedure, otherwise false</param>
		/// <returns>An IDbCommand</returns>
		internal IDbCommand CreateCommand(string sqlText, bool procedure)
		{
			IDbCommand cmd = _connection.CreateCommand();
			cmd.CommandText = sqlText;
			cmd.Transaction = _transaction;
			if(procedure)
				cmd.CommandType = CommandType.StoredProcedure;
			return cmd;
		}

		/// <summary>
		/// Creates the connection to the Database
		/// </summary>
		/// <returns>an IDbConnection</returns>
		protected virtual IDbConnection CreateConnection()
		{
			string str = ConfigurationSettings.AppSettings["ConnectionString"];
			if (str == null || str.Length <= 0)
			{
                return new System.Data.OracleClient.OracleConnection("Server=DB10G.DELTADATOR.INET;User ID=sugvc01;Password=sugvc01;");
			}
			return new System.Data.OracleClient.OracleConnection(str);
		}

		/// <summary>
		/// Fills a DataTable using an IDbCommand
		/// </summary>
		/// <param name="command">The IDbCommand</param>
		/// <returns>A filled DataTable</returns>
		protected internal DataTable CreateDataTable(IDbCommand command)
		{
			DataTable dataTable = new DataTable();
			new System.Data.OracleClient.OracleDataAdapter((System.Data.OracleClient.OracleCommand)command).Fill(dataTable);
			return dataTable;
		}

		/// <summary>
		/// Creates a SQL parameter
		/// </summary>
		/// <param name="paramName">The name of the parameter</param>
		/// <returns>The SQL parameter</returns>
		protected internal string CreateSqlParameterName(string paramName)
		{
			return ":" + paramName;
		}

