		/// </summary>
		/// <param name="baseParamName">The name of the parameter</param>
		/// <returns>The Collection SQL parameter</returns>
		protected string CreateCollectionParameterName(string baseParamName)
		{
			return ":" + baseParamName;
		}

		/// <summary>
		/// Creates an IDbCommand
		/// </summary>
		/// <param name="sqlText">The SQL statement for the IDbCommand</param>
		/// <returns>An IDbCommand</returns>
		internal IDbCommand CreateCommand(string sqlText)
		{
			return CreateCommand(sqlText, false);
		}

		/// <summary>
		/// Closes the connection to the Database
		/// </summary>
		public virtual void Close()
		{
			if(_connection != null)
				_connection.Close();
		}

		/// <summary>
		/// Default destructor
		/// </summary>
		~GVC()
		{
			Dispose(false);
		}

		/// <summary>
		/// Disposes the current object
		/// </summary>
		public virtual void Dispose()
		{
			Dispose(true);
		}

		/// <summary>
		/// Disposes the current object
		/// </summary>
		/// <param name="disposing">true if the object is disposing, otherwise false</param>
		protected virtual void Dispose(bool disposing)
		{
			if(disposing)
			{
				if (this._transaction != null)
					this._transaction.Dispose();

				if(this._connection != null)
					this._connection.Dispose();

				GC.SuppressFinalize(this);
			}
		}

